import React from 'react';

function Home() {

  const redirect = (role) => {
    window.location.href = `/login/${role}`;
  };

  return (
    <div
      style={{
        height: '100vh',
        display: 'flex',
        alignItems: 'flex-start',
        justifyContent: 'center',
        color: '#fff',
        paddingTop: '100px',
        background: 'linear-gradient(135deg, #9b5de5, #6f42c1)' // soft purple gradient background
      }}
    >
      <div
        className="login-box"
        style={{
          background: '#fff',
          color: '#333',
          borderRadius: '20px',
          padding: '60px 40px',
          boxShadow: '0 4px 25px rgba(0,0,0,0.2)',
          width: '100%',
          maxWidth: '430px',
          minHeight: '480px', // ðŸ‘ˆ elongates the box vertically
          textAlign: 'center'
        }}
      >
        <h3 className="fw-bold mb-5" style={{ color: '#6f42c1' }}>Login</h3>

        <button
          onClick={() => redirect('student')}
          style={{
            width: '100%',
            marginTop: '15px',
            fontWeight: 600,
            borderRadius: '10px',
            padding: '14px',
            background: 'linear-gradient(90deg, #b388ff, #7c4dff)',
            border: 'none',
            color: '#fff',
            transition: '0.3s'
          }}
          onMouseOver={e => e.target.style.background = 'linear-gradient(90deg, #9c6bff, #6a1b9a)'}
          onMouseOut={e => e.target.style.background = 'linear-gradient(90deg, #b388ff, #7c4dff)'}
        >
          Student
        </button>

        <button
          onClick={() => redirect('vendor')}
          style={{
            width: '100%',
            marginTop: '15px',
            fontWeight: 600,
            borderRadius: '10px',
            padding: '14px',
            background: 'linear-gradient(90deg, #c77dff, #9d4edd)',
            border: 'none',
            color: '#fff',
            transition: '0.3s'
          }}
          onMouseOver={e => e.target.style.background = 'linear-gradient(90deg, #b15cff, #7b2cbf)'}
          onMouseOut={e => e.target.style.background = 'linear-gradient(90deg, #c77dff, #9d4edd)'}
        >
          Vendor
        </button>

        <button
          onClick={() => redirect('admin')}
          style={{
            width: '100%',
            marginTop: '15px',
            fontWeight: 600,
            borderRadius: '10px',
            padding: '14px',
            background: 'linear-gradient(90deg, #bb86fc, #6200ea)',
            border: 'none',
            color: '#fff',
            transition: '0.3s'
          }}
          onMouseOver={e => e.target.style.background = 'linear-gradient(90deg, #a56eff, #4a00b5)'}
          onMouseOut={e => e.target.style.background = 'linear-gradient(90deg, #bb86fc, #6200ea)'}
        >
          Admin
        </button>
      </div>
    </div>
  );
}

export default Home;
