import React, { useState } from 'react';
import StudentDashboardLayout from './StudentDashboardLayout'; // adjust path

function StudentAddItem() {
  const [title, setTitle] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [availability, setAvailability] = useState('');

  useEffect(() => {
  const fetchItems = async () => {
    try {
      const res = await fetch(`http://127.0.0.1:5000/items/student/${studentID}`);
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error(err);
    }
  };
  fetchItems();
}, []);
const handleAddItem = async (e) => {
  e.preventDefault();
  const formData = new FormData();
  formData.append("title", newItem.title);
  formData.append("description", newItem.description);
  formData.append("price", newItem.price);
  formData.append("availability", newItem.availability);
  if (newItem.image) formData.append("image", newItem.image);

  try {
    const res = await fetch(`http://127.0.0.1:5000/items/student/${studentID}`, {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    if (res.ok) {
      setItems([...items, data]);
      setNewItem({ title: "", description: "", price: "", availability: "", image: null });
      setShowModal(false);
    } else {
      alert(data.error);
    }
  } catch (err) {
    console.error(err);
    alert("Failed to add item.");
  }
};

const handleDelete = async (itemID) => {
  if (!window.confirm("Are you sure?")) return;
  try {
    await fetch(`http://127.0.0.1:5000/items/${itemID}`, { method: "DELETE" });
    setItems(items.filter(i => i.itemID !== itemID));
  } catch (err) {
    console.error(err);
  }
};

const handleQuantityChange = async (itemID, newQuantity) => {
  setItems(items.map(i => i.itemID === itemID ? {...i, availability: newQuantity} : i));
  await fetch(`http://127.0.0.1:5000/items/${itemID}`, {
    method: "PATCH",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ availability: newQuantity }),
  });
};

const handlePriceChange = async (itemID, newPrice) => {
  setItems(items.map(i => i.itemID === itemID ? {...i, price: newPrice} : i));
  await fetch(`http://127.0.0.1:5000/items/${itemID}`, {
    method: "PATCH",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ price: newPrice }),
  });
};

  return (
    <StudentDashboardLayout activePage="add_item">
      <h4 className="fw-semibold mb-3">Add an Item to Sell</h4>

      <form onSubmit={handleAddItem}>
        <div className="row g-3">
          <div className="col-md-6">
            <label className="form-label">Item Title</label>
            <input
              type="text"
              name="title"
              className="form-control"
              required
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Price (â‚¹)</label>
            <input
              type="number"
              name="price"
              step="0.01"
              min="0"
              className="form-control"
              required
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Category</label>
            <input
              type="text"
              name="category"
              className="form-control"
              required
              value={category}
              onChange={(e) => setCategory(e.target.value)}
            />
          </div>

          <div className="col-md-6">
            <label className="form-label">Availability (Stock)</label>
            <input
              type="number"
              name="availability"
              min="0"
              className="form-control"
              required
              value={availability}
              onChange={(e) => setAvailability(e.target.value)}
            />
          </div>
        </div>

        <div className="mt-3">
          <button type="submit" className="btn btn-success">Add Item</button>
        </div>
      </form>
    </StudentDashboardLayout>
  );
}

export default StudentAddItem;
