import React from 'react';

function Home() {

  // Redirect function for buttons
  const redirect = (role) => {
    window.location.href = `/login/${role}`;
  };

  return (
    <div
  style={{
    height: '100vh',
    display: 'flex',
    alignItems: 'flex-start', // ðŸ‘ˆ move upward
    justifyContent: 'center',
    color: '#fff',
    paddingTop: '120px' // ðŸ‘ˆ controls how far down from the top it sits
  }}
>

      <div
        className="login-box"
        style={{
          background: '#fff',
          color: '#333',
          borderRadius: '15px',
          padding: '40px 30px',
          boxShadow: '0 0 20px rgba(0,0,0,0.2)',
          width: '100%',
          maxWidth: '420px',
          textAlign: 'center'
        }}
      >
        <h3 className="fw-bold mb-4">Login</h3>
        <button
          className="btn btn-primary btn-role"
          onClick={() => redirect('student')}
          style={{ width: '100%', marginTop: '10px', fontWeight: 500, borderRadius: '10px', padding: '12px' }}
        >
          Student
        </button>
        <button
          className="btn btn-success btn-role"
          onClick={() => redirect('vendor')}
          style={{ width: '100%', marginTop: '10px', fontWeight: 500, borderRadius: '10px', padding: '12px' }}
        >
          Vendor
        </button>
        <button
          className="btn btn-dark btn-role"
          onClick={() => redirect('admin')}
          style={{ width: '100%', marginTop: '10px', fontWeight: 500, borderRadius: '10px', padding: '12px' }}
        >
          Admin
        </button>
      </div>
    </div>
  );
}

export default Home;
