import React from 'react';
import { Link } from 'react-router-dom'; // if using React Router

function StudentDashboardLayout({ activePage, currentUser = { name: 'Student' }, children }) {
  const navLinks = [
    { name: 'Marketplace', path: '/student/marketplace', key: 'marketplace' },
    { name: 'My Items', path: '/student/my_items', key: 'sell' },
    { name: 'My Cart', path: '/student/cart', key: 'cart' },
    { name: 'My Orders', path: '/student/orders', key: 'orders' },
    { name: 'Forum', path: '/student/forum', key: 'forum' },
  ];

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        className="sidebar"
        style={{
          height: '100vh',
          width: '240px',
          backgroundColor: '#1a1d20',
          color: '#fff',
          display: 'flex',
          flexDirection: 'column',
          paddingTop: '1rem',
        }}
      >
        <h4 style={{ textAlign: 'center', color: '#00bfa6', marginBottom: '1rem' }}>
          Student Panel
        </h4>

        {navLinks.map((link) => (
          <Link
            key={link.key}
            to={link.path}
            className={`nav-link ${activePage === link.key ? 'active' : ''}`}
            style={{
              color: activePage === link.key ? '#fff' : '#bbb',
              margin: '6px 0',
              borderRadius: '8px',
              padding: '6px 12px',
              backgroundColor: activePage === link.key ? '#198754' : 'transparent',
              textDecoration: 'none',
            }}
          >
            {link.name}
          </Link>
        ))}

        <Link
          to="/login/student"
          className="nav-link"
          style={{ color: 'red', marginTop: 'auto', padding: '6px 12px' }}
        >
          Logout
        </Link>
      </div>

      {/* Content Area */}
      <div
        className="content"
        style={{ flex: 1, padding: '2rem', marginLeft: '260px' }}
      >
        <h3 className="fw-semibold text-success">Welcome, {currentUser.name} ðŸ‘‹</h3>
        <p className="text-muted">
          Use the sidebar to navigate between marketplace and forum features.
        </p>

        {/* Page-specific content */}
        {children}
      </div>
    </div>
  );
}

export default StudentDashboardLayout;
