import React, { useState } from 'react';
import Layout from './components/Layout'; // adjust path if needed
import { useNavigate } from 'react-router-dom';

function StudentLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    console.log({ email, password });
    // TODO: Call backend API for student login
    // On success, navigate to student dashboard
    // navigate('/student/dashboard');
  };

  return (
    <Layout>
      <div
        style={{
          height: '80vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: '#f8f9fa',
          paddingTop: '50px',
        }}
      >
        <div
          style={{
            background: '#fff',
            borderRadius: '20px',
            border: '4px solid #6f42c1', // purple border
            padding: '50px 40px',
            width: '100%',
            maxWidth: '400px',
            boxShadow: '0 4px 25px rgba(0,0,0,0.15)',
            textAlign: 'center',
          }}
        >
          <h2
            style={{
              color: '#6f42c1',
              fontSize: '2rem',
              fontWeight: '700',
              marginBottom: '30px',
            }}
          >
            Student Login
          </h2>

          <form onSubmit={handleLogin}>
            <div className="mb-3 text-start">
              <label className="form-label">Email</label>
              <input
                type="email"
                name="email"
                className="form-control"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            <div className="mb-3 text-start">
              <label className="form-label">Password</label>
              <input
                type="password"
                name="password"
                className="form-control"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            <button
              type="submit"
              style={{
                width: '100%',
                marginTop: '20px',
                fontWeight: 700,
                borderRadius: '12px',
                padding: '15px',
                fontSize: '1.2rem',
                background: 'linear-gradient(90deg, #b388ff, #7c4dff)',
                border: 'none',
                color: '#fff',
                transition: '0.3s',
              }}
              onMouseOver={(e) =>
                (e.target.style.background = 'linear-gradient(90deg, #9c6bff, #6a1b9a)')
              }
              onMouseOut={(e) =>
                (e.target.style.background = 'linear-gradient(90deg, #b388ff, #7c4dff)')
              }
            >
              Login
            </button>
          </form>

          <p className="mt-3">
            Not registered?{' '}
            <a href="/student/register" style={{ color: '#6f42c1', fontWeight: '600' }}>
              Create an account
            </a>
          </p>
        </div>
      </div>
    </Layout>
  );
}

export default StudentLogin;
