import React from 'react';
import { Link } from 'react-router-dom';

function StudentDashboardLayout({ activePage, currentUser = { name: 'Student' }, children }) {
  const navLinks = [
    { name: 'Marketplace', path: '/student/marketplace', key: 'marketplace' },
    { name: 'My Items', path: '/student/my_items', key: 'sell' },
    { name: 'My Cart', path: '/student/cart', key: 'cart' },
    { name: 'My Orders', path: '/student/orders', key: 'orders' },
    { name: 'Forum', path: '/student/forum', key: 'forum' },
  ];

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar */}
      <div
        className="sidebar"
        style={{
          height: '100vh',
          width: '240px',
          backgroundColor: '#fff',
          borderRight: '4px solid #6f42c1', // purple border
          display: 'flex',
          flexDirection: 'column',
          paddingTop: '1rem',
        }}
      >
        <h4 style={{ textAlign: 'center', color: '#6f42c1', marginBottom: '1rem' }}>
          Student Panel
        </h4>

        {navLinks.map((link) => (
          <Link
            key={link.key}
            to={link.path}
            className={`nav-link ${activePage === link.key ? 'active' : ''}`}
            style={{
              color: '#6f42c1', // purple text
              margin: '6px 12px',
              borderRadius: '8px',
              padding: '8px 12px',
              backgroundColor: activePage === link.key ? '#d3b3ff' : 'transparent', // light purple if active
              textDecoration: 'none',
              transition: '0.3s',
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#e6ccff'; // light purple on hover
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor =
                activePage === link.key ? '#d3b3ff' : 'transparent';
            }}
          >
            {link.name}
          </Link>
        ))}

        {/* Logout */}
        <Link
          to="/login/student"
          className="nav-link"
          style={{
            color: 'red',
            marginTop: 'auto',
            marginLeft: '12px',
            padding: '8px 12px',
            borderRadius: '8px',
            textDecoration: 'none',
          }}
        >
          Logout
        </Link>
      </div>

      {/* Content Area */}
      <div
        className="content"
        style={{
          flex: 1,
          padding: '2rem',
          marginLeft: '260px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          textAlign: 'center',
        }}
      >
        <h2 style={{ color: '#6f42c1', fontWeight: '700', fontSize: '2rem', marginBottom: '1rem' }}>
          Welcome, {currentUser.name} ðŸ‘‹
        </h2>
        <p style={{ color: '#9d4edd', fontSize: '1.1rem' }}>
          Use the sidebar to navigate between marketplace and forum features.
        </p>

        {/* Page-specific content */}
        {children}
      </div>
    </div>
  );
}

export default StudentDashboardLayout;
